let playing_accounts_count=0,
playing_spotify_account,
playing_spotcast_account,
speaker_media_content_id,
speaker_friendly_name;

if(msg.is_it_speaker_in_group===true)
{
    speaker_media_content_id=msg.speakers_groups_data[msg.which_speaker_group].attributes.media_content_id;
    speaker_friendly_name=msg.speakers_groups_data[msg.which_speaker_group].attributes.friendly_name;
}
else
{
    speaker_media_content_id=msg.speaker_data.attributes.media_content_id;
    speaker_friendly_name=msg.speaker_data.attributes.friendly_name;
}

for(i=0;i<msg.spotify_accounts_count;i++)
{
    if(!msg.compare_spotify_speaker_name)
    {
        if(msg.spotify_accounts_data[i].state==="playing")
        {
            playing_accounts_count++;
            playing_spotify_account=msg.spotify_accounts[i];
            playing_spotcast_account=msg.spotcast_accounts[i];
        }
        if(speaker_media_content_id===msg.spotify_accounts_data[i].attributes.media_content_id)
        {
            msg.is_right_account_found=true;
            msg.right_spotify_account=msg.spotify_accounts[i];
            msg.right_spotify_account_source=msg.spotify_accounts_data[i].attributes.source;
            msg.right_spotcast_account=msg.spotcast_accounts[i];
            break;
        }
    }
    else
    {
        if(speaker_friendly_name===msg.spotify_accounts_data[i].attributes.source)
        {
            msg.is_right_account_found=true;
            msg.right_spotify_account=msg.spotify_accounts[i];
            msg.right_spotify_account_source=msg.spotify_accounts_data[i].attributes.source;
            msg.right_spotcast_account=msg.spotcast_accounts[i];
            break;
        }
    }
}
if(!msg.compare_spotify_speaker_name && !msg.spotify_require_identical_media_content_id && !msg.is_right_account_found && playing_accounts_count==1)
{
    msg.is_right_account_found=true;
    msg.right_spotify_account=playing_spotify_account;
    msg.right_spotcast_account=playing_spotcast_account;
}
return msg;

if(msg.is_it_speaker_in_group===true)
{
    msg.tts_speaker=msg.speaker;
    msg.resume_speaker=msg.speakers_groups[msg.which_speaker_group];
    msg.resume_speaker_friendly_name=msg.speakers_groups_data[msg.which_speaker_group].attributes.friendly_name;
}
else
{
    msg.tts_speaker=msg.speaker;
    msg.resume_speaker=msg.speaker;
    msg.resume_speaker_friendly_name=msg.speaker_data.attributes.friendly_name;
}

return msg;

if(msg.debug)
{
    node.warn("Smart TTS debug output: ");
    node.warn(msg);
}

let speaker_id=msg.speaker;

if(msg.is_it_speaker_in_group===true)
{
    speaker_id=msg.speakers_groups_data[msg.which_speaker_group].entity_id;
}

for(i=0;i<msg.ytube_accounts_count;i++)
{
    if(msg.media_type=="ytube" && speaker_id==msg.ytube_accounts_data[i].attributes.remote_player_id && msg.ytube_accounts_data[i].state==="playing")
    {
        msg.is_right_account_found=true;
        msg.right_ytube_account=msg.ytube_accounts[i];
        break;
    }
    else if(msg.media_type=="YouTube" && speaker_id==msg.ytube_accounts_data[i].attributes.remote_player_id)
    {
        msg.is_right_account_found=true;
        msg.right_ytube_account=msg.ytube_accounts[i];
        break;
    }
}
if(msg.media_type=="YouTube" && !msg.is_right_account_found)
{
    for(i=0;i<msg.ytube_accounts_count;i++)
    {
        if(msg.media_type=="YouTube" && msg.ytube_accounts_data[i].state!=="playing")
        {
            msg.is_right_account_found=true;
            msg.right_ytube_account=msg.ytube_accounts[i];
            break;
        }
    }
}
return msg;

node.warn("Smart TTS ERROR: ");
node.warn(msg.error);
if(msg.debug)
{
    node.warn("Smart TTS debug output: ");
    node.warn(msg);
}

for(i=0;i<msg.speakers_groups_count;i++)
{
    if(msg.speaker===msg.speakers_groups[i])
    {
        msg.is_it_group=true;
        msg.is_it_speaker_in_group=false;
        msg.which_speaker_group=i;
        break;
    }
}
if(msg.is_it_group===false)
{
    for(i=0;i<msg.speakers_groups_count;i++)
    {
        if(msg.speaker_data.state===msg.speakers_groups_data[i].state && 
        msg.speaker_data.attributes.media_content_id===msg.speakers_groups_data[i].attributes.media_content_id && 
        msg.speakers_groups_data[i].state==="playing")
        {
            for(j=0;j<msg.speakers_inside_groups[i].length;j++)
            {
                if(msg.speaker===msg.speakers_inside_groups[i][j])
                {
                    msg.is_it_group=false;
                    msg.is_it_speaker_in_group=true;
                    msg.which_speaker_group=i;
                    break;
                }
            }
        }
        if(msg.is_it_speaker_in_group===true)break;
    }
}
return msg;

function find_track(tracks, index)
{
    let position = tracks.search(" - ");
    tracks = tracks.substring(position + 3, tracks.length);
    msg.substr_tracks.push(tracks);
    if(tracks == msg.speaker_data.attributes.media_title)
    {
        return index;
    }
}
msg.substr_tracks = [];
let track = msg.ytube_extra_data.attributes.tracks.findIndex(find_track);
if(track > -1)
{
    if (track <= msg.max_ytube_track_skips)
    {
        msg.ytube_track_index = track;
    }
    else
    {
        msg.ytube_track_index = 0;
        node.warn("Track is too far in the album");
    }

}
else
{
    msg.ytube_track_index = 0;
}
return msg;

