//Media players ID's from Spotify integration
//If you don't use spotify make it empty like example below
//msg.spotify_accounts=[];
//(Remember about removing example media players)
msg.spotify_accounts =
    [
        "media_player.spotify_account_1",
        "media_player.spotify_account_2",
        "media_player.spotify_account_3",
        "media_player.spotify_account_4"
    ];

//Accounts from Spotcast integration
//(In the same order as spotify accounts)
//If you don't use spotify make it empty like example below
//msg.spotcast_accounts=[];
//(Remember about removing example media players)
msg.spotcast_accounts =
    [
        "default",
        "account_2",
        "account_3",
        "account_4"
    ];

//Media players ID's from ytube_music_player integration 
//If you don't use YouTube music make it empty like example below
//msg.ytube_accounts=[];
//(Remember about removing example media players)
msg.ytube_accounts =
    [
        "media_player.ytube_account_1",
        "media_player.ytube_account_2",
        "media_player.ytube_account_3",
        "media_player.ytube_account_4"
    ];

//Define dafult Google TTS language
const default_tts_language = "en" 

//Speakers groups ID's
//If you don't use groups make it empty like example below
//msg.speakers_groups=[];
//(Remember about removing example media players)
msg.speakers_groups =
    [
        "media_player.group1",
        "media_player.group2",
        "media_player.group3"
    ];

//Speakers inside groups ID's
//(In the same order as speakers groups ID's)
//If you don't use groups make it empty like example below
//msg.speakers_inside_groups=[];
//(Remember about removing example media players)
msg.speakers_inside_groups =
    [
        [//Speakers in media_player.group1
            "media_player.speaker1_in_group1",
            "media_player.speaker2_in_group1",
            "media_player.speaker3_in_group1"
        ],
        [//Speakers in media_player.group2
            "media_player.speaker1_in_group2",
            "media_player.speaker2_in_group2",
            "media_player.speaker3_in_group2"
        ],
        [//Speakers in media_player.group3
            "media_player.speaker1_in_group3",
            "media_player.speaker2_in_group3",
            "media_player.speaker3_in_group3",
            "media_player.speaker4_in_group3"
        ]
    ];

//Default TTS service like google_translate_say or reversotts_say
//For Alexa it's alexa_default
const default_tts_service = "google_translate_say";

//Pause media when calling TTS on Alexa speaker
msg.alexa_pause_on_tts = true;

// +----------+
// | Advanced |
// +----------+

//If the speaker and Spotify has other media ID but 
//the Spotify account is the only one playing 
//and speaker is playing from Spotify
//smart TTS is using this Spotify account to resume.
//Changing this variable on true will disable this function 
msg.spotify_require_identical_media_content_id = false;

//Compare speaker name with Spotify source name instead
//of comparing media content ID when looking for Spotify
//account which is playing on the speaker
msg.compare_spotify_speaker_name = true;

//Use Spotify speaker name instead of HA media player ID
//when using Spotcast service
//(Speaker friendly name and Spotify source
//speaker name must be this same)
msg.use_spotify_speaker_name = true;

//Smart TTS have to skip all tracks in the album
//from first one to the one it wants to resume.
//This number defines how many skips it can make.
msg.max_ytube_track_skips = 100

//Delete variables used in smart TTS on end
msg.delete_variables = true;

//Enable debug output
msg.debug = false;


// +----------------+
// | DO NOT CHANGE! |
// +----------------+

let is_config_ok = true,
    spotify_exist = true,
    spotcast_exist = true,
    speakers_groups_exist = true,
    speakers_inside_groups_exist = true;
msg.is_it_group = false;
msg.is_it_speaker_in_group = false;

if (typeof (msg.speaker) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: Speaker ID is missing!');
}
if (typeof (msg.message) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: TTS message is missing!');
}
if (typeof (default_tts_service) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "default_tts_service" is missing!');
}
if (typeof (msg.spotify_require_identical_media_content_id) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.spotify_require_identical_media_id" is missing!');
}
if (typeof (msg.compare_spotify_speaker_name) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.compare_spotify_speaker_name" is missing!');
}
if (typeof (msg.use_spotify_speaker_name) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.use_spotify_speaker_name" is missing!');
}
if (typeof (msg.delete_variables) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.delete_variables" is missing!');
}
if (typeof (msg.debug) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.debug" is missing!');
}
if (typeof (msg.spotify_accounts) === 'undefined') {
    is_config_ok = false;
    spotify_exist = false;
    node.warn('Smart TTS: "msg.spotify_accounts[]" is missing!');
}
if (typeof (msg.spotcast_accounts) === 'undefined') {
    is_config_ok = false;
    spotcast_exist = false;
    node.warn('Smart TTS: "msg.spotcast_accounts[]" is missing!');
}
if (typeof (msg.ytube_accounts) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.ytube_accounts[]" is missing!');
}
if (typeof (msg.speakers_groups) === 'undefined') {
    is_config_ok = false;
    speakers_groups_exist = false;
    node.warn('Smart TTS: "msg.speakers_groups[]" is missing!');
}
if (typeof (msg.speakers_inside_groups) === 'undefined') {
    is_config_ok = false;
    speakers_inside_groups_exist = false;
    node.warn('Smart TTS: "msg.speakers_inside_groups[]" is missing!');
}
if (typeof (msg.alexa_pause_on_tts) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.alexa_pause_on_tts" is missing!');
}
if (typeof (msg.max_ytube_track_skips) === 'undefined') {
    is_config_ok = false;
    node.warn('Smart TTS: "msg.max_ytube_track_skips" is missing!');
}

if (spotify_exist && spotcast_exist) {
    if (msg.spotify_accounts.length !== msg.spotcast_accounts.length) {
        is_config_ok = false;
        node.warn('Smart TTS: "msg.spotify_accounts[]" and "msg.spotcast_accounts[]" must have the same number of variables!');
    }
}
if (speakers_groups_exist && speakers_inside_groups_exist) {
    if (msg.speakers_groups.length !== msg.speakers_inside_groups.length) {
        is_config_ok = false;
        node.warn('Smart TTS: "msg.speakers_groups[]" and "msg.speakers_inside_groups[]" must have the same number of variables!');
    }
}


if (is_config_ok) {
    msg.spotify_accounts_count = msg.spotify_accounts.length;
    msg.spotify_accounts_data = new Array(msg.spotify_accounts_count);
    msg.ytube_accounts_count = msg.ytube_accounts.length;
    msg.ytube_accounts_data = new Array(msg.ytube_accounts_count);
    msg.speakers_groups_count = msg.speakers_groups.length;
    msg.speakers_groups_data = new Array(msg.speakers_groups_count);
    if (typeof (msg.tts_service) === 'undefined') msg.tts_service = default_tts_service;
    return msg;
}
